"use client";

import {
  Card,
  Text,
  Title,
  Group,
  Image,
  Badge,
  Container,
  Box,
} from "@mantine/core";
import { useEffect, useRef } from "react";

export default function ActivitySessionFeed() {
  const activities = [
    {
      title: "Book Reading Session",
      description: "Join our storytime and reading fun",
      image: "https://images.unsplash.com/photo-1512820790803-83ca734da794?crop=entropy&cs=tinysrgb&fit=max&w=400",
    },
 
    // {
    //   title: "Cooking Workshop",
    //   description: "Learn quick and easy recipes",
    //   image: "https://images.unsplash.com/photo-1600891964599-f61ba0e24092?crop=entropy&cs=tinysrgb&fit=max&w=400",
    // },
    {
      title: "Yoga Session",
      description: "Morning yoga for relaxation",
      image: "https://blog.cdn.level.game/2024/06/world-yoga-day-2024.webp",
    },

    {
      title: "Art & Craft",
      description: "Get creative with painting and crafts",
      image: "https://d11kvfv4kxw5s4.cloudfront.net/wp-content/uploads/sites/15/2021/05/27140852/header-1-1.jpg",
    },
    {
      title: "Music Jam",
      description: "Live music session for all ages",
      image: "https://images.unsplash.com/photo-1507874457470-272b3c8d8ee2?crop=entropy&cs=tinysrgb&fit=max&w=400",
    },
    // {
    //   title: "Fitness Challenge",
    //   description: "Push yourself in our fun fitness session",
    //   image: "https://images.unsplash.com/photo-1599058917218-5f26b6d0f9b0?crop=entropy&cs=tinysrgb&fit=max&w=400",
    // },
    {
      title: "Photography Workshop",
      description: "Learn tips & tricks for photography",
      image: "data:image/jpeg;base64,",
    },
    {
      title: "Dance Session",
      description: "Join our fun dance workout",
      image: "https://images.unsplash.com/photo-1518609878373-06d740f60d8b?crop=entropy&cs=tinysrgb&fit=max&w=400",
    },

        {
      title: "VR Gaming",
      description: "Experience immersive VR games",
      image: "https://gamepalacio.com/wp-content/uploads/2024/11/Why-VR-Gaming-Zones-are-the-Perfect-Destination-for-Fun-_-scaled.jpg",
    },
  ];

  const list = [...activities, ...activities]; // for infinite scroll
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const el = scrollRef.current;
    if (!el) return;

    let scroll = 0;
    const interval = setInterval(() => {
      scroll += 1; // scroll speed
      el.scrollLeft = scroll;
      if (scroll >= el.scrollWidth / 2) {
        scroll = 0;
        el.scrollLeft = 0;
      }
    }, 20);

    return () => clearInterval(interval);
  }, []);

  return (
    <Container size="xxl" py="xl">
      {/* <Title order={3} mb="sm">
        ðŸ”¥ Live Activity Sessions Near You
      </Title>
      <Text size="sm" c="dimmed" mb="md">
        Explore ongoing activities and sessions in your nearby stores
      </Text> */}


      <Box ta="center" mb={30}>
        <Title order={2} fw={800}>
           Live Activity Sessions Near You
        </Title>
        <Text c="dimmed" size="sm" fw={500}>
          Explore ongoing activities and sessions in your nearby stores
        </Text>
      </Box>



      <div
        ref={scrollRef}
        style={{
          overflow: "hidden",
          whiteSpace: "nowrap",
        }}
      >
        <Group gap="sm" wrap="nowrap">
          {list.map((item, index) => (
            <Card
              key={index}
              style={{
                maxWidth: "250px",
                flexShrink: 0,
                position: "relative",
                padding: 0,
                overflow: "hidden",
              }}
            >
              <Image
                src={item.image}
                height={150}
                width="100%"
                alt={item.title}
                style={{ objectFit: "cover" }}
                radius={0}
                // withPlaceholder
              />
              {/* Overlay Text */}
              <div
                style={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  width: "100%",
                  background: "linear-gradient(to top, rgba(0,0,0,0.7), rgba(0,0,0,0))",
                  color: "white",
                  padding: "8px",
                }}
              >
                <Group  align="center" >
                  <Text fw={600} size="sm">
                    {item.title}
                  </Text>
                  <Badge size="xs" color="green" variant="filled">
                    LIVE
                  </Badge>
                </Group>
                <Text size="xs" mt={2}>
                  {item.description}
                </Text>
              </div>
            </Card>
          ))}
        </Group>
      </div>
    </Container>
  );
}






